}

ReactDOM.render(
  <div>
      <App name="王瑄好正" handleClick={printMessage}/> {/* 綁定函示 */}
      <div id="show-area"></div> {/*這裡是等等用來改變的地方*/}
  </div>,
  document.getElementById('root')
);


