import React, { Component } from 'react';

class Baby extends Component{
    constructor(props){
        super(props);
        this.state={
            isRightDad:true,
            isGetData:false,
            Mom:''
        }
        this.ajaxSimulator = this.ajaxSimulator.bind(this);
        this.scrollTo = this.scrollTo.bind(this);
    }
    ajaxSimulator(){
        setTimeout(()=>{this.setState({isGetData:true,Mom:'WS'})},3000);
    }
    scrollTo(){
        /* 讀取 container元素中的scrollLeft */
        let scrollLeft = document.getElementById('container').scrollLeft;
        if(scrollLeft<500){
            /* 修改container元素中的scrollLeft */
            document.getElementById('container').scrollLeft = scrollLeft + 5;
            setTimeout(this.scrollTo,60);
        }
    }

    static getDerivedStateFromProps(props,state){
        if(props.dad!=='Shih')
            return {isRightDad:false}
    }
    componentDidMount(){
        this.ajaxSimulator();
        this.scrollTo();
      }

        render(){
            return(
                <div>
                    <div id="msg">
                        {(this.state.isGetData===false)?"讀取中":"他媽媽是"+this.state.Mom}
                    </div>
                    <div id="container" style={{width:"100px",overflowX:"scroll"}}>
                        <div id="left"  style={{width:"700px",fontSize:"30px",textAlign:"center"}}>
                            {"我是頭 O->---------------< 我是腳，我滑出來囉~ "}
                        </div>
                    </div>
                </div>
            )
        }
}
export default Baby;
