import React, { Component, useState } from 'react';

const Baby=(props)=>{
    /* 把state變成useState */
    const [isRightDad,setRightDad]=useState(false);
    const [isGetData,setGetData]=useState(false);
    const [Mom,setMom]=useState("");

    /* 把class 的 member function改成function中的function */
    const ajaxSimulator=()=>{
        setTimeout(()=>{
            setGetData(true);
            setMom("正妹");
        },3000)
    }
    const checkDad=()=>{
        if(props.dad==="Shih")
            setRightDad(true)
        else
            setRightDad(false)
    }

    if(isRightDad===false)
        return(
            <div>他的媽媽是誰，干你屁事</div>
        );
    else if(isGetData===false)
        return(
            <div id="msg">讀取中</div>
        );
    else
        return(
            <div id="msg">他的媽媽是{Mom}</div>
        );

}
export default Baby;
