    constructor(props){
        super(props);
        this.state={
            isRightDad:true
        }
    }

    static getDeriveStateFromProps(props,state){
        if(props.dad=='Shih')
            return {isRightDad:false}
    }
    render(){
        if(this.state.isRightDad===true)
            return(
                <div>他媽媽是王瑄</div>
            );
        else
            return(
                <div>關你Ｘ事</div>
            );
    }
}
